import  React from "react";
import Item from './item'
import {connect} from  "react-redux"
 class Mian extends React.Component{
    render(){
        console.log(this.props);
        let data = this.props.data;
        return(
            <table className="main"
                   style={{
                       display: data.length ? "table" : "none"
                   }}
            >
                <thead>
                <tr>
                    <th>
                        <input
                            type="checkbox"
                            id="checkAll"
                            checked={this.props.isCheckAll }
                            onChange={(e)=>{
                                this.props.dispatch({
                                    type:"CHECK_ALL",
                                    checked:e.target.checked
                                })
                            }}
                        />
                        <label htmlFor="checkAll">全选</label>
                    </th>
                    <th>歌曲</th>
                    <th>歌手</th>
                    <th>收藏</th>
                    <th>删除</th>
                </tr>
                </thead>
                <tbody>
                {
                    data.map((val,index)=>{
                        return (
                            <Item
                                key={index}
                                data={val}
                                index = {val.id}

                            />
                        )
                    })
                }

                </tbody>
            </table>
        );
    }
}

/*
* 根据pathName 判断当前应该显示什么列表
* checkAll 判断是否是全选状态
*
 */
export default connect((state,props)=>{
    console.log(state);
    let isCheckAll = (function () {
         for(var i = 0; i < state.data.length; i++){
               if(!state.data[i].selected){
                   return false;
               }
               return  true;
          }
    })();
    return  Object.assign({},state,{isCheckAll});
})(Mian);
