import React from 'react';
import ReactDOM from 'react-dom';
import { Link,Route,Redirect } from 'react-router-dom';
import Main from './main'
import Footer from './foot'


export  default  class Home extends  React.Component{

    constructor(props){
        super(props)
    }
    render(){
        let props = this.props;
        let data = props.data;
        let selectData = data.filter((val)=>val.selected);
        let likeData = data.filter((val)=>val.like);

        return(
            <div>
                <header>
                    <h2 className="title">{props.pathName === '/' ? "播放列表" : "收藏列表"}
                    <Link to="/add" className="addLink">添加歌曲</Link>{/* 执行跳转 */}
                    </h2>
                </header>
                <Link to="/">所有列表</Link>
                <Link to="/like">收藏列表</Link>
                <Route path="/" exact render={()=>{
                    return (
                        <Main
                            data = {data}
                            checkAll={props.checkAll}  // 检测是否全部选中
                            setCheckAll={props.setCheckAll}   // 全选
                            setCheck={props.setCheck}      // 单选
                            setLike = {props.setLike}      // 收藏
                            remove = {props.remove}
                        />
                    );
                }}/>
                <Route path="/like" exact render={()=>{
                    if(likeData.length === 0){
                        return  (
                            <Redirect to="/"/> // 重定向
                        );
                    }
                    return (
                        <Main
                            data = {likeData}
                            checkAll={props.checkAll}  // 检测是否全部选中
                            setCheckAll={props.setCheckAll}   // 全选
                            setCheck={props.setCheck}      // 单选
                            setLike = {props.setLike}      // 收藏
                            remove = {props.remove}
                        />
                    );
                }}/>
                <Footer
                   // 取消收藏选中
                />
            </div>
        );
    }
}
