import  { combineReducers } from "redux";

let data = (data=[],action)=>{ // 处理添加数据的逻辑
    switch (action.type) {
        case "ADD":
            return [...data,{
                id:Date.now(),
                title:action.title,
                singer:action.singer,
                selected:false,
                like:false
            }]
        case "REMOVE":
            return data.filter((item)=>item.id != action.id);
    }
    console.log(action);
    return data;
}

let reducer = combineReducers({
    data
});

export  default  reducer;
