import  { combineReducers } from "redux";

let data = (data=[{
    id:1,
    title:"天气不错",
    singer:"sdsds",
    selected:true,
    like:false
},{
    id:2,
    title:"告白地球",
    singer:'Jay',
    selected:true,
    like:false
}],action)=>{ // 处理添加数据的逻辑
    switch (action.type) {
        case "ADD":
            return [...data,{ // 参数1. ...data 展开原始数组  参数2：添加一个新对象
                id:Date.now(),
                title:action.title,
                singer:action.singer,
                selected:true,
                like:false
            }]
        case "REMOVE":
            return data.filter((item)=>item.id != action.id);
        case "CHECK_ALL":
            return data.map((item)=>{
                item.selected = action.checked;
                return item;
            })
        case "CHECK":
            return data.map((item)=>{
                item.selected = action.checked;
                return item;
            })

    }
    console.log(action);
    return data;
}

let reducer = combineReducers({
    data
});

export  default  reducer;
